import React, { useContext } from 'react';
import UrunContext from '../context/UrunContext';
export default function UrunEkle() {

  const { urunEkle } = useContext(UrunContext);

  const handleSubmit = (e) => {
    e.preventDefault();
    const urun = {
      urunAdi: e.target.urunAdi.value,
      urunAciklamasi: e.target.urunAciklamasi.value
    }
    urunEkle(urun);
  }


  return (
    <>
    <h4 className="mt-5">ÜRÜN EKLE</h4>
    <div className="card">
      <div className="card-body">
        <div className="mb-3">
          <label htmlFor="urunAdi" className="form-label">Ürün Adı</label>
          <input type="text" className="form-control" id="urunAdi" />
        </div>
        <div className="mb-3">
          <label htmlFor="urunAciklamasi" className="form-label">Açıklama</label>
          <textarea className="form-control" id="urunAciklamasi" rows="3"></textarea>
        </div>
        <div className="col-auto" style={{ textAlign: "right" }}>
          <button type="submit" className="btn btn-primary mb-3">Ekle</button>
        </div>
      </div>
    </div>
    </>
  )
}
